package shane.leetcode.problems.xhard;

import org.junit.jupiter.api.Test;

import static org.assertj.core.api.Assertions.assertThat;

/**
 * Not my solution
 */
public class Q1092ShortestCommonSupersequence {

    @Test
    public void test() {
        assertThat(shortestCommonSupersequence("abac", "cab")).isEqualTo("cabac");
        assertThat(shortestCommonSupersequence("aaaaaaaa", "aaaaaaaa")).isEqualTo("aaaaaaaa");
        assertThat(shortestCommonSupersequence("bbbaaaba", "bbababbb")).isEqualTo("bbbaaababbb");
    }

    @Test
    public void MLE() {
        assertThat(shortestCommonSupersequence(
                "",
                ""))
                .isEqualTo("");
    }

    public String shortestCommonSupersequence(String str1, String str2) {
        String common = longestCommonSubSeq(str1, str2);
        int p1 = 0, p2 = 0;
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < common.length(); i++, p1++, p2++) {
            while (p1 < str1.length() && str1.charAt(p1) != common.charAt(i)) {
                sb.append(str1.charAt(p1++));
            }
            while (p2 < str2.length() && str2.charAt(p2) != common.charAt(i)) {
                sb.append(str2.charAt(p2++));
            }
            sb.append(common.charAt(i));
        }
        sb.append(str1.substring(p1)).append(str2.substring(p2));
        return sb.toString();
    }

    private String longestCommonSubSeq(String str1, String str2) {
        int m = str1.length(), n = str2.length();
        int[][] dp = new int[m + 1][n + 1];

        for (int i = 1; i <= m; i++) {
            for (int j = 1; j <= n; j++) {
                if (str1.charAt(i - 1) == str2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1] + 1;
                } else {
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1]);
                }
            }
        }

        StringBuilder lcs = new StringBuilder();
        int i = m, j = n;
        while (i > 0 && j > 0) {
            if (str1.charAt(i - 1) == str2.charAt(j - 1)) {
                lcs.append(str1.charAt(i - 1));
                i--;
                j--;
            } else if (dp[i - 1][j] > dp[i][j - 1]) {
                i--;
            } else {
                j--;
            }
        }
        return lcs.reverse().toString();
    }

}
